/*jslint bitwise: true, unparam: true, maxerr: 50, white: true */
/*globals exports:true, module, require, process */
var child_process = require('child_process');

function growl(title, message, icon, callback) {
	"use strict";
	if (!callback && icon instanceof Function) {
		callback = icon;
		icon = undefined;
	}
	child_process.exec(
			'growlnotify "' + title + '" -m"' + message + '"' + (icon ? " --image '" + icon + "'": ""),
			function () {
				if (callback) { callback(); }
			});
}

exports.send = growl;

